import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Navbar from './navbar/Navbar';
import SliderHome from './Slider';
import './Home.css';
import Footer from './footer/Footer';
import SearchBar from './shop/SearchBar';
import { useCartDispatch } from './CartContext';

const Home = () => {

  const dispatch = useCartDispatch();
  const addToCart = (product) => {
    dispatch({ type: 'ADD_TO_CART', payload: product });
    navigate('./Cart');
  };
  const [products, setProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [cartItems, setCartItems] = useState([]);

  const [products2, setProducts2] = useState([]);  /// second API



  const imageStyle = {
    width: '100%',
    height: 'auto',
    maxHeight: '140px',
    border:'2px Solid green',
    boxShadow:'2px 2px cyan',
  };

  const navigate = useNavigate();
  const clickButton = () => {
    navigate('./paymentpage');
  };



  const handleSearch = (event) => {
    setSearchQuery(event.target.value);
  };

  // Filter products based on the search query
  const filteredProducts = products.filter((product) =>
    product.title.toLowerCase().includes(searchQuery.toLowerCase())
  );


  return (
    <>
      <Navbar />
      <div>
        <div className="search-bar">
          <SearchBar searchQuery={searchQuery} handleSearch={handleSearch} />
          <section className="home">
            <div className="container d_flex">
              <SliderHome />
            </div>
          </section>
        </div>
        {loading && <p>Loading...</p>}
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {!loading && !error && (


          <div className="product-list Aa">
            {filteredProducts.map((product) => (
              <div key={product.id} className="product-card">
                <img src={product.image} alt={product.title} style={imageStyle} />
                <h3>{product.title}</h3>
                <p>${product.price}</p>
                <button onClick={() => addToCart(product)} className="btn btn-warning m-2">
                  ADD TO CART
                </button>
                <button onClick={clickButton} className="btn btn-primary">
                  BUY NOW
                </button>
              </div>
            ))}
          </div>
        )}

        {/* ////////  Second API fatch product data */}

        <div className="product-list Aa">
          {/* {products2.map((product) => ( */}
            <div key={products2.id} className="product-card">
              <img src={products2.thumbnail} alt={products2.title} style={imageStyle} />
              <h3>{products2.title}</h3>
              <p>${products2.price}</p>
              <button onClick={() => addToCart(products2)} className="btn btn-warning m-2">
                ADD TO CART
              </button>
              <button onClick={clickButton} className="btn btn-primary">
                BUY NOW
              </button>
            </div>
          {/* // ))} */}
        </div>

      </div>
      <Footer />
    </>
  );
};

export default Home;
