import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';
import { Link, useNavigate } from "react-router-dom";
import Navbar from './navbar/Navbar';
import SliderHome from './Slider';
import Footer from './footer/Footer';
import SearchBar from './shop/SearchBar';

import 'bootstrap/dist/css/bootstrap.min.css';
import './Home.css';
import imageStyle from './javasc.js';

const Home = () => {
  const navigate = useNavigate();

  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedEmployees, setSelectedEmployees] = useState('');
  const [selectedExperience, setSelectedExperience] = useState('');
  const [selectedLocation, setSelectedLocation] = useState('');
  const [selectedSalary, setSelectedSalary] = useState('');

  const [products, setProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const handleEmployeesChange = (event) => {
    setSelectedEmployees(event.target.value);
  };

  const handleExperienceChange = (event) => {
    setSelectedExperience(event.target.value);
  };

  const handleLocationChange = (event) => {
    setSelectedLocation(event.target.value);
  };

  const handleSalaryChange = (event) => {
    setSelectedSalary(event.target.value);
  };

  const handleCheckout = async () => {
    const { isConfirmed } = await Swal.fire({
      icon: "success",
      title: "Your job application has been successfully submitted!",
      showCancelButton: true,
      confirmButtonText: 'Continue',
    });
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post(
          'https://api.weekday.technology/adhoc/getSampleJdJSON',
          {
            limit: 10,
            offset: 0
          },
          {
            headers: {
              'Content-Type': 'application/json'
            }
          }
        );
        setProducts(response.data.jdList);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching products:', error);
        setError('Error fetching products. Please try again later.');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const clickButton = (product) => {
    navigate(`/details`, { state: { selectedProduct: product } });
  };

  const handleSearch = (event) => {
    setSearchQuery(event.target.value);
  };

  const filteredProducts = products.filter((product) => {
    return (
      product.jobRole.toLowerCase().includes(searchQuery.toLowerCase())&&product.jobRole.toLowerCase().includes(searchQuery.toLowerCase()) &&
      (selectedCategory === '' || product.jobRole.toLowerCase().includes(selectedCategory.toLowerCase())) &&
      (selectedEmployees === '' || product.employees.toLowerCase() === selectedEmployees.toLowerCase()) &&
      (selectedExperience === '' || product.experience.toLowerCase() === selectedExperience.toLowerCase()) &&
      (selectedLocation === '' || product.location.toLowerCase() === selectedLocation.toLowerCase()) &&
      (selectedSalary === '' || (product.minJdSalary >= parseInt(selectedSalary) && product.maxJdSalary <= parseInt(selectedSalary)))
    );
  });

  return (
    <>
      <Navbar />
      <div>
        <div className="search-bar">
          <div className="Aa">
            <center>
              <div className="d-flex justify-content-center align-items-center text-center self">
                <Link className="nav-link self2" exact={true} to="/">
                  <strong>All Jobs</strong>
                </Link>

                <select className="selected" value={selectedCategory} onChange={handleCategoryChange}>
                  <option value="" disabled selected>Roles</option>
                  <option value="">Backend </option>
                  <option value="">Frontend</option>
                  <option value="">Fullstack</option>
                  <option value="">IOS</option>
                  <option value="">Android</option>
                  <option value="">Data Science</option>
                  <option value="">Machine Learning Engineer</option>
                  <option value="">DevOps</option>
                  <option value="">Cybersecurity</option>
                  <option value="">Product Manager</option>
                  <option value="">UI/UX Designer</option>
                  <option value="">QA Engineer</option>
                  <option value="">Project Manager</option>
                  <option value="">Business Analyst</option>
                  <option value="">Sales</option>
                  <option value="">Marketing</option>
                  <option value="">Customer Support</option>
                  <option value="">Human Resources</option>
                  <option value="">Finance</option>
                  <option value="">Operations</option>
                  <option value="">Content Creator</option>
                  <option value="">Digital Marketing</option>
                  <option value="">Graphic Designer</option>
                  <option value="">Remote</option>
                  <option value="">Freelance</option>
                </select>

                <select className="selected" value={selectedEmployees} onChange={handleEmployeesChange}>
                  <option value="" disabled selected>Number Of Employees</option>
                  <option value="">1-10</option>
                  <option value="">11-20</option>
                  <option value="">21-50</option>
                  <option value="">51-100</option>
                  <option value="">101-200</option>
                  <option value="">201-500</option>
                  <option value="">1000+</option>
                </select>

                <select className="selected" value={selectedExperience} onChange={handleExperienceChange}>
                  <option value="" disabled selected>Experience</option>
                  <option value="">0</option>
                  <option value="">1</option>
                  <option value="">2</option>
                  <option value="">3</option>
                  <option value="">4</option>
                  <option value="">5</option>
                  <option value="">6</option>
                </select>

                <select className="selected" value={selectedLocation} onChange={handleLocationChange}>
                  <option value="">Remote</option>
                  <option value="">Hybrid</option>
                  <option value="">In-office</option>
                </select>

                <select className="selected" value={selectedSalary} onChange={handleSalaryChange}>
                  <option value="" disabled selected>Minimum Base Pay Salary</option>
                  <option value="">0L</option>
                  <option value="">10L</option>
                  <option value="">20L</option>
                  <option value="">30L</option>
                  <option value="">40L</option>
                  <option value="">50L</option>
                  <option value="">60L</option>
                </select>
              </div>
            </center>
          </div>
          <SearchBar searchQuery={searchQuery} handleSearch={handleSearch} />
          <section className="home">
            <div className="container d_flex">
              <SliderHome />
            </div>
          </section>
        </div>
        {loading && <p>Loading...</p>}
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {!loading && !error && (
          <div className="product-list Aa">
            {filteredProducts.map((product) => (
              <div key={product.jdUid} className="product-card">
                <img src={product.logoUrl} alt={product.companyName} style={imageStyle} onClick={() => clickButton(product)} />
                <h3>{product.companyName}</h3>
                <p>{product.jobRole}</p>
                <p>${product.minJdSalary} - ${product.maxJdSalary}</p>
                <p>Location: {product.location}</p>
                <p>MinExperience: {product.minExp}</p>
                <p></p>
                <button onClick={() => clickButton(product)} className="btn btn-primary m-2">
                  View Job
                </button>
                <button className="btn btn-green btt" onClick={handleCheckout}>
                  ðŸ”¥Easy Apply
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
      <Footer />
    </>
  );
};

export default Home;
