import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';
import { Link, useNavigate } from "react-router-dom";
import Navbar from './navbar/Navbar';
import SliderHome from './Slider';
import Footer from './footer/Footer';
import SearchBar from './shop/SearchBar';

import 'bootstrap/dist/css/bootstrap.min.css';
import './Home.css';
import imageStyle from './javasc.js';

const Home = () => {
  const navigate = useNavigate();

  const [selectedCategory, setSelectedCategory] = useState('');
  const [products, setProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Search criteria state variables
  const [selectedEmployees, setSelectedEmployees] = useState('');
  const [selectedExperience, setSelectedExperience] = useState('');
  const [selectedLocation, setSelectedLocation] = useState('');
  const [selectedSalary, setSelectedSalary] = useState('');

  // Function to handle category change
  const handleCategoryChange = (event) => {
    const selectedCategoryValue = event.target.value;
    setSelectedCategory(selectedCategoryValue);
    navigate(`/AllCategories`, { state: { selectedCategory: selectedCategoryValue } });
  };

  // Function to handle search query change
  const handleSearch = (event) => {
    setSearchQuery(event.target.value);
  };

  // Function to handle checkout
  const handleCheckout = async () => {
    const { isConfirmed } = await Swal.fire({
      icon: "success",
      title: "Your job application has been successfully submitted!",
      showCancelButton: true,
      confirmButtonText: 'Continue',
    });
  };

  // Function to handle filtering based on search criteria
  const filterProducts = () => {
    return products.filter(product => {
      return (
        product.title.toLowerCase().includes(searchQuery.toLowerCase()) &&
        (selectedCategory === '' || product.category === selectedCategory) &&
        (selectedEmployees === '' || product.employees === selectedEmployees) &&
        (selectedExperience === '' || product.experience === selectedExperience) &&
        (selectedLocation === '' || product.location === selectedLocation) &&
        (selectedSalary === '' || (product.salary >= parseInt(selectedSalary.split('-')[0], 10) && product.salary <= parseInt(selectedSalary.split('-')[1], 10)))
      );
    });
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post(
          'https://api.weekday.technology/adhoc/getSampleJdJSON',
          {
            limit: 10,
            offset: 0
          },
          {
            headers: {
              'Content-Type': 'application/json'
            }
          }
        );
        setProducts(response.data.jdList);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching products:', error);
        setError('Error fetching products. Please try again later.');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const [selectedProduct, setSelectedProduct] = useState(null);

  const clickButton = (product) => {
    setSelectedProduct(product);
    navigate(`/details`, { state: { selectedProduct: product } });
  };

  return (
    <>
      <Navbar />
      <div>
        <div className="search-bar">
          {/* Search criteria UI */}
          {/* Add similar select elements for other criteria */}
          <select value={selectedEmployees} onChange={(e) => setSelectedEmployees(e.target.value)}>
            <option value="">Number Of Employees</option>
            <option value="1-10">1-10</option>
            <option value="11-20">11-20</option>
            {/* Add other options */}
          </select>
          <SearchBar searchQuery={searchQuery} handleSearch={handleSearch} />
          <section className="home">
            <div className="container d_flex">
              <SliderHome />
            </div>
          </section>
        </div>
        {loading && <p>Loading...</p>}
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {!loading && !error && (
          <div className="product-list Aa">
            {/* Filtered product list */}
            {filterProducts().map((product) => (
              <div key={product.jdUid} className="product-card">
                <img src={product.logoUrl} alt={product.companyName} style={imageStyle} onClick={() => clickButton(product)} />
                <h3>{product.companyName}</h3>
                <p>{product.jobRole}</p>
                <p>${product.minJdSalary} - ${product.maxJdSalary}</p>
                <p>Location: {product.location}</p>
                <p>MinExperience: {product.minExp}</p>
                <p></p>
                <button onClick={() => clickButton(product)} className="btn btn-primary m-2">
                  View Job
                </button>
                <button className="btn btn-green btt" onClick={handleCheckout}>
                  ðŸ”¥Easy Apply
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
      <Footer />
    </>
  );
};

export default Home;
