// Cart.js
import React from 'react';
import { useCartState, useCartDispatch } from './CartContext';
import Navbar from './navbar/Navbar';
import { useNavigate } from 'react-router-dom';

const Cart = () => {
  const cartItems = useCartState();
  const dispatch = useCartDispatch();
  const navigate=useNavigate();

  const handleDelete = (productId) => {
    // Dispatch an action to remove the item from the cart
    dispatch({ type: 'REMOVE_FROM_CART', payload: productId });
  };

  const handleCheckout = () => {
    navigate("./Payment");
  };

  const crt ={
    padding:"4px",
    background:"#74ebd5",
    bachgroung:'linear-gradient(to right, #ACB6E5, #74ebd5)',
  };

  return (

    <div>
      <Navbar/>
      <center>
      <h2 style={crt}>Shopping Cart ðŸ›’</h2>
      </center>
      <div>

      <ul>
        {cartItems.map((item) => (
            <li key={item.id}>
            {item.title} - ${item.price}
            <button onClick={() => handleDelete(item.id)} className="btn btn-danger">Delete</button>
          </li>
        ))}
      </ul>
      <center>
        <button onClick={handleCheckout} className='btn btn-success'>Proceed to Checkout</button>
      </center>
    </div>
    </div>
  );
};

export default Cart;
