import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Navbar from './navbar/Navbar';
import SliderHome from './Slider';
import './Home.css';
import Footer from './footer/Footer';
import SearchBar from './shop/SearchBar';
import { useCartDispatch } from './CartContext';

const Home = () => {

  const navigate = useNavigate();
  const dispatch = useCartDispatch();
  const addToCart = (product) => {
    dispatch({ type: 'ADD_TO_CART', payload: product });
    navigate('./Cart');
  };
  const [products, setProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [cartItems, setCartItems] = useState([]);

  const [products2, setProducts2] = useState([]);  /// second API

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response1 = await axios.get('https://fakestoreapi.com/products'); // First API
        setProducts(response1.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching products:', error);
        setError('Error fetching products. Please try again later.');
        setLoading(false);
      }
    };

    const fetchProducts2 = async () => {
      try {
        const response2 = await axios.get('https://dummyjson.com/products?limit=100'); // second API
        setProducts2(response2.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching products from second endpoint:', error);
        setError('Error fetching products from second endpoint. Please try again later.');
        setLoading(false);
      }
    };

    fetchProducts();
    fetchProducts2();
  }, []);


  const imageStyle = {
    width: '100%',
    height: 'auto',
    maxHeight: '140px',
    border: '2px Solid green',
    boxShadow: '2px 2px cyan',
  };


  const clickButton = () => {
    navigate('./paymentpage');
  };



  const handleSearch = (event) => {
    setSearchQuery(event.target.value);
  };

  // Filter products based on the search query
  const filteredProducts = products.filter((product) =>
    product.title.toLowerCase().includes(searchQuery.toLowerCase())
  );


  return (
    <>
      <Navbar />
      <div>
        <div className="search-bar">
          <SearchBar searchQuery={searchQuery} handleSearch={handleSearch} />
          <section className="home">
            <div className="container d_flex">
              <SliderHome />
            </div>
          </section>
        </div>
             {/* {loading && <p>Loading...</p>}
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {!loading && !error && (


          <div className="product-list Aa">
            {filteredProducts.map((product) => (
              <div key={product.id} className="product-card">
                <img src={product.image} alt={product.title} style={imageStyle} />
                <h3>{product.title}</h3>
                <p>${product.price}</p>
                <button onClick={() => addToCart(product)} className="btn btn-warning m-2">
                  ADD TO CART
                </button>
                <button onClick={clickButton} className="btn btn-primary">
                  BUY NOW
                </button>
              </div>
            ))}
          </div>
           )} */}
      </div>
      {/* ////////  Second API fatch product data */}
      <div>
        <h2>Product List</h2>
        <ul>
          {products2.map((product12) => (
            <li key={product12.id}>
              <img src={product12.thumbnail} alt={product12.title} style={imageStyle} />
              <h3>{product12.title}</h3>
              <p>Description: {product12.description}</p>
              <p>Price: ${product12.price}</p>
              <p>Rating: {product12.rating}</p>
              <p>Brand: {product12.brand}</p>
            </li>
          ))}
        </ul>
      </div>
      <Footer />
    </>
  );
};

export default Home;
