import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {  useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Navbar from '../navbar/Navbar';
import '../Home.css';
import SliderHome from '../Slider';
import Footer from '../footer/Footer';
import SearchBar from './SearchBar';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');


  useEffect(() => {
    const fetchProducts = async () => {
      try {
        // const response = await axios.get('https://fakestoreapi.com/products');
        // const response = await axios.get('https://fakestoreapi.com/products/category/men%27s%20clothing');
        // const response = await axios.get('https://api.escuelajs.co/api/v1/products');
        const response = await axios.get('https://api.escuelajs.co/api/v1/categories');

        // const response = await axios.get('https://dummyjson.com/carts');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const imageStyle = {
    width: "100%",  
    height: "auto", 
    maxHeight: "140px", // Set a maximum height if needed
  };

  const navigate=useNavigate();
  const clickButton=()=>{
    navigate("./paymentpage")
  }
  const clickButton1=()=>{
    navigate("./cart")
  }

  const handleSearch = (event) => {
    setSearchQuery(event.target.value);
  };

  // Filter products based on the search query
  const filteredProducts = products.filter((product) =>
    product.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <>
    <Navbar/>
    <div>
    <div className="search-bar">
         <SearchBar searchQuery={searchQuery} handleSearch={handleSearch} />
          <section className="home">
          <div className="container d_flex">
            <SliderHome />
          </div>
          </section>
    </div>
    <div className="product-list Aa">
        {filteredProducts./*slice(0, 51)*/map((product) => (
          <div key={product.id} className="product-card">
          <img src={product.image} alt={product.title} style={imageStyle} />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>{product.brand}</p>
            <p>${product.price}</p>
            <button onClick={clickButton1} className='btn btn-warning m-2'>ADD TO CART</button>
            <button onClick={clickButton} className='btn btn-primary'>BUY NOW</button>
          </div>
        ))}
      </div>
    </div>
    <Footer/>
    </>
  );
};

export default Products;
