          <section className="home">
          <div className="container d_flex">
            <SliderHome />
          </div>
          </section>
      </div>
    
    <div className="product-list Aa">
        {products./*slice(0, 51)*/map((product) => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.title} style={imageStyle}/>
            <h3>{product.title}</h3>
            <p>${product.price}</p>
            <button onClick={clickButton1} className='btn btn-warning m-2'>ADD TO CART</button>
            <button onClick={clickButton} className='btn btn-primary'>BUY NOW</button>
          </div>
        ))}
      </div>
    </div>
    <Footer/>
    </>
  );
};

export default Jewelery;
