import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import slider1 from './slider1.jpg';

const SliderHome = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: false,
    autoplaySpeed: 3000,
  };

  const imageStyle = {
    width: "100%",  // Set the width to 100% to make the image responsive
    height: "auto", // Maintain the aspect ratio
    maxHeight: "400px", // Set a maximum height if needed
  };

  return (
    <Slider {...settings} className="ecommerce-slider">
      <div>
        <img src={slider1} alt="Slide 1" style={imageStyle} />
      </div>
      <div>
        <img src={slider1} alt="Slide 2" style={imageStyle} />
      </div>
      <div>
        <img src={slider1} alt="Slide 3" style={imageStyle} />
      </div>
    </Slider>
  );
};

export default SliderHome;
