import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import Axios from 'axios';
import './login.css';
import Navbar from './navbar/Navbar';

const Signup = () => {


  const location = useLocation();
  const selectedDate = location.state?.selectedDate;
  console.log(selectedDate);



  const [credentials, setCredentials] = useState({
    email: '',
    password: '',
  });

  const [isChecked, setIsChecked] = useState(false); // New state for the checkbox
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials({
      ...credentials,
      [name]: value,
    });
  };

  const handleCheckboxChange = (e) => {
    setIsChecked(e.target.checked);
  };
  const [selectedFlight, setSelectedFlight] = useState([]); // Updated state for selectedFlight


  const handleLogin = async (e) => {
    e.preventDefault();

    if (!isChecked) {
      alert('Please agree to the Policy and Terms of Use.');
      return;
    }

    try {
      const response = await Axios.post('http://localhost:3001/login', {
        email: credentials.email,
        password: credentials.password,
      });

      console.log(response.data);

      if (response.data.success) {

        alert('Login successful!');

        // Check if flight data is present in location state
        if (location.state && location.state.selectedFlight) {
          // Redirect to Passenger component
          navigate('/booking', {
            state: {
              username: response.data.username,
              selectedFlight: location.state.selectedFlight,
              selectedDate: selectedDate,
            },
          });
          console.log(setSelectedFlight);
          console.log(selectedFlight);
        }
        else {
          // Redirect to userSuccess component
          navigate('/userSuccess', {
            state: {
              username: response.data.username,
            },
          });
        }

      }
      else {
        alert('Invalid credentials. Please try again.');
      }
    } catch (error) {
      console.error('Error during login:', error);
      alert('Error during login. Please try again.');
    }
  };

  return (
    <>
      <Navbar />
      <div className='AAA'>
        <div className="container">
          <div className='card text-black m-5' style={{ borderRadius: '25px' }}>
            <div className='card-body'>
              <center>
                <div className='col-md-10 col-lg-6 order-2 order-lg-1 d-flex flex-column align-items-center '>

                  <p className="text-center h2 fw-bold mb-5 mx-1 mx-md-4 mt-4 ">LogIn</p>

                  <div className='col-md-10 col-lg-6 order-2 order-lg-1 d-flex flex-column align-items-center '>
                <p className="text-center h2 fw-bold mb-2 mx-1 mx-md-4 mt-1">Sign up</p>

                <div className="mb-0">
                  <label htmlFor="name" className="form-label">Name:</label>
                  <div className="input-group">
                    <input
                      id="name"
                      name='name'
                      type='text'
                      placeholder='Your Name'
                      className='form-control rounded-3'
                      value={formData.name}
                      onChange={handleChange}
                    />
                  </div>
                </div>

                <div className="mb-0">
                  <label htmlFor="email" className="form-label">Email:</label>
                  <div className="input-group">
                    <input
                      id="email"
                      name='email'
                      type='email'
                      placeholder='Your Email'
                      value={formData.email}
                      onChange={handleChange}
                      className='form-control rounded-3'
                    />
                  </div>
                </div>

                <div className="mb-0">
                  <label htmlFor="password" className="form-label">Password:</label>
                  <div className="input-group">
                    <input
                      id="password"
                      name='password'
                      type='password'
                      placeholder='Password'
                      value={formData.password}
                      onChange={handleChange}
                      className='form-control rounded-3'
                    />
                  </div>
                </div>

                <div className="mb-0">
                  <label htmlFor="repeatPassword" className="form-label">Repeat Password:</label>
                  <div className="input-group">
                    <input
                      id="repeatPassword"
                      name='repeatPassword'
                      type='password'
                      placeholder='Repeat your password'
                      value={formData.repeatPassword}
                      onChange={handleChange}
                      className='form-control rounded-3'
                    />
                  </div>
                </div>

                <div className='mb-0'>
                  <input
                    type='checkbox'
                    id='subscribe'
                    name='subscribe'
                    checked={formData.subscribe}
                    onChange={handleChange}
                  /><label className='form-check-label ms-2 d-inline' htmlFor='subscribe'>Agree with the Policy and Terms of Use</label>
                </div>


                <button className='btn btn-primary mb-0' onClick={handleSubmit}>Register</button>
                <div>
                <Link to="/login">
                  <h5 className="text-green-600" > Already a User ? Login</h5>
                </Link>
                </div>
              </div>
                </div>
              </center>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Signup;
