// Cart.js
import React from 'react';
import { useCartState, useCartDispatch } from './CartContext';
import Navbar from './navbar/Navbar';
import { useNavigate } from 'react-router-dom';

const Cart = () => {
  const cartItems = useCartState();
  const dispatch = useCartDispatch();
  const navigate=useNavigate();

  const handleDelete = (productId) => {
    // Dispatch an action to remove the item from the cart
    dispatch({ type: 'REMOVE_FROM_CART', payload: productId });
  };

  const handleCheckout = () => {
    navigate("./Payment");
  };

  const crt ={
    padding:"4px",
    background:"#74ebd5",
    bachgroung:'linear-gradient(to right, #ACB6E5, #74ebd5)',
  };
  const imageStyle = {
    width: "3%",  
    height: "auto", 
    maxHeight: "100px",
  };

  return (

    <div>
      <Navbar/>
      <center>
      <h2 style={crt}>Shopping Cart ðŸ›’</h2>
      </center>
      <div>
        <ul style={{ paddingLeft: '0px', listStyleType: 'none' }}>
          {cartItems.map((item) => (
            <li key={item.id} className='border'>
              <img src={item.image} alt={item.title} style={imageStyle} />
              <div style={{ display: 'inline-block' }}>
                <p style={{ fontSize: 'large', fontWeight: 'bold' }}>{item.title} --- ${item.price}</p>
                <button onClick={() => handleDelete(item.id)} className='btn btn-danger m-2'>
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
        <center>
          <button onClick={handleCheckout} className='btn btn-success'>
            Proceed to Checkout
          </button>
        </center>
      </div>
    </div>
  );
};

export default Cart;
