import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import imageStyle from "./javasc";
import Navbar from "./navbar/Navbar";
import './details.css';
const Details = (products) => {
    const navigate = useNavigate();
    const location = useLocation();
    const { selectedProduct } = location.state || {};


    return (
        <>
            <Navbar />
            <div>
                <center>
                    <h2 style={{ padding: "10px", background: "" }}>Product</h2>
                </center>
                <div>
                    <center>
                        <div className="product-list Aa">
                            {selectedProduct.jdList.map((product) => (
                                <div key={product.jdUid} className="product-card">
                                    <img src={product.logoUrl} alt={product.companyName} style={imageStyle} />
                                    <h3>{product.companyName}</h3>
                                    <p>{product.jobRole}</p>
                                    <p>${product.minJdSalary} - ${product.maxJdSalary}</p>
                                    <p>Location: {product.location}</p>
                                    <p>MinExperience: {product.minExp}</p>
                                    <p></p>
                                    <button  className="btn btn-primary m-2">
                                        View Job
                                    </button>
                                    <button  className="btn btn-green btt">
                                        ðŸ”¥Easy Apply
                                    </button>
                                </div>
                            ))}

                        </div>
                    </center>
                </div>
            </div>
        </>
    );
};

export default Details;
