import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './style/Home.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import Navbar from "./navbar/Navbar";
// import Categories from './Categories';
import SliderHome from './Slider';
import './Home.css';

const Home = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        // const response = await axios.get('https://fakestoreapi.com/products');
        const response = await axios.get('https://api.escuelajs.co/api/v1/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const imageStyle = {
    width: "100%",  
    height: "auto", 
    maxHeight: "150px", // Set a maximum height if needed
  };

  

  return (
    <>
    <Navbar/>
    <div>
    <section className='home'>
        <div className='container d_flex'>
          {/* <Categories /> */}
          <SliderHome />
        </div>
    </section>
    <div className="product-list">
        {products.slice(0, 51).map((product) => (
          <div key={product.id} className="product-card">
            <img src={product.images } alt={product.title} style={imageStyle}/>
            <h3>{product.title}</h3>
            <p>${product.price}</p>
            <button onClick={clickButton} className='btn btn-primary'>BUY NOW</button>
          </div>
        ))}
      </div>
    </div>
    </>
  );
};

export default Home;
