import React from 'react';

const ProductDetails = ({ product }) => {
  const {
    id,
    title,
    description,
    price,
    discountPercentage,
    rating,
    stock,
    brand,
    category,
    thumbnail,
    images,
  } = product;

  return (
    <div>
      <h2>{title}</h2>
      <p>ID: {id}</p>
      <p>Description: {description}</p>
      <p>Price: ${price}</p>
      <p>Discount Percentage: {discountPercentage}%</p>
      <p>Rating: {rating}</p>
      <p>Stock: {stock}</p>
      <p>Brand: {brand}</p>
      <p>Category: {category}</p>

      {/* Display Thumbnail */}
      <img src={thumbnail} alt={title} style={{ width: '100px' }} />

      {/* Display Images (you can use a gallery or carousel here) */}
      <div>
        {images.map((image, index) => (
          <img key={index} src={image} alt={`${title}-image-${index}`} style={{ width: '100px', margin: '5px' }} />
        ))}
      </div>
    </div>
  );
};

export default ProductDetails;
