import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductForm from './ProductForm'; // Assuming you have a ProductForm component for adding/editing products

const Admin = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('https://fakestoreapi.com/products');
        setProducts(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching products:', error);
        setError('Error fetching products. Please try again later.');
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  const addProduct = (newProduct) => {
    setProducts([...products, newProduct]);
    // You might want to make a POST request to add the product to your backend database
  };

  const editProduct = (editedProduct) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) => (product.id === editedProduct.id ? editedProduct : product))
    );
    // You might want to make a PUT request to update the product in your backend database
  };

  const deleteProduct = (productId) => {
    setProducts((prevProducts) => prevProducts.filter((product) => product.id !== productId));
    // You might want to make a DELETE request to remove the product from your backend database
  };

  return (
    <div>
      <h2>Admin Page</h2>
      {/* Display a form to add a new product */}
      <ProductForm onSubmit={addProduct} />

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {!loading && !error && (
        <div>
          <h3>Product List</h3>
          <ul>
            {products.map((product) => (
              <li key={product.id}>
                {product.title} - ${product.price}
                <button onClick={() => editProduct(product)}>Edit</button>
                <button onClick={() => deleteProduct(product.id)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Admin;
